const mongoose = require("mongoose");

const postSchema = new mongoose.Schema({
    title: {
        type: String,
        unique: true,
     
      
    },
    descreption: {
        type: String,
      
    },
     imgUrl: {
        type: String, 
        default:
            'data:image/jpeg;base64,',
    },
    comment: {
        type: mongoose.Types.ObjectId,
        ref: "Comment",
    },
}, { timestamps: true });

module.exports = mongoose.model("Post", postSchema);
